import { Pipe, PipeTransform } from "@angular/core";
import { last } from "rxjs";

@Pipe({name: 'zeroByPointBar'})
export class ZeroByPointBar implements PipeTransform {
    transform(value: any) {
        let lastChar = value.getSeconds();
        console.log(lastChar);
        return value;
    }
}
