import { Pipe, PipeTransform } from "@angular/core";

@Pipe({name: 'zeroByPointBar'})
export class ZeroByPointBar implements PipeTransform {
    transform(value: any) {
        if(value.slice(-1) === '0' || value.slice(-1) === 0){
            return value.replace('0', '!');
        }
        return value;
    }
}
