import { Component, OnInit, numberAttribute } from '@angular/core';
import { Voiture } from '../model/hero.model';


@Component({
  selector: 'app-hero-list',
  templateUrl: './hero-list.component.html',
  styleUrls: ['./hero-list.component.scss']
})
export class HeroListComponent implements OnInit {
      isDisplayed : boolean = false;
      name : string = "AHAHAH";
      voitures : Array<Voiture> = new Array<Voiture>();
      count : number = 0;
      private _titre : string = "TITREEEEEE"; 

      get title(): string{
        return this._titre;
      }

      set title(val: string){

        this._titre = val
      }

      constructor(){ }

      ngOnInit(): void {
          setTimeout(()=> {
            this.isDisplayed = true;
          }, 5000)

          this.voitures.push(new Voiture("Peugeot", "106", 1998));
          this.voitures.push(new Voiture("Renault", "T16 Turbo", 1980));
          this.voitures.push(new Voiture("Lancia", "S4", 1980));     
      }
      onButtonClicked(nbToAdd : number){
        this.count+= nbToAdd;
      }



}

<h1>{{titre}}</h1>

<p>{{ name }}</p>

<div *ngIf="isDisplayed">
    <p>COUCOU</p>
</div>


<table>
    <thead>
        <tr>
            <th>Marque</th>
            <th>Modele</th>
            <th>Année</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let voiture of voitures">
            <td>{{voiture.marque}}</td>
            <td>{{voiture.modele}}</td>
            <td>{{voiture.annee}}</td>
        </tr>

    </tbody>
</table>


<p>Somme des boutons {{count}}</p>

<button type="button" (click)="onButtonClicked(1)">+1</button>
<button type="button" (click)="onButtonClicked(-1)">-1</button>
<button type="button" (click)="onButtonClicked(2)">+2</button>
